import React, { Component } from 'react';
import axios from 'axios';
import ReactPaginate from 'react-paginate';
import 'bootstrap/dist/css/bootstrap.css';
import './table.css'; // Import the CSS file


class PaginatedTable extends Component {
  state = {
    data: [], // Store fetched data here
    currentPage: 1,
    itemsPerPage: 10, // Number of items to display per page
  };

  componentDidMount() {
    fetch('http://localhost:8050/api/v1/getallbookmarks?page=1&pageSize=38') // Replace with your API endpoint
      .then((response) => response.json())
      .then((data) => this.setState({ data }));
  }

  render() {
    // Pagination logic (skip in this example)
    const { data, currentPage, itemsPerPage } = this.state;

    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    const currentData = data.slice(startIndex, endIndex);

    return (
      <div>
        <table>
          <thead>
            <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Description</th>
            <th>Link / Details</th>
            <th>User</th>
            <th>tagName</th>
            </tr>
          </thead>
          <tbody>
            {currentData.map((item, index) => (
              <tr key={index}>
                <td>{item.name}</td>
                <td>{item.description}</td>
                <td>{item.link}</td>
                <td>{item.user.email}</td>
                <td>{item.tag.name}</td>
                {/* Add more table cells based on your data structure */}
              </tr>
            ))}
          </tbody>
        </table>
        <div className="pagination-container">
        <ReactPaginate
          pageCount={Math.ceil(data.length / itemsPerPage)}
          pageRangeDisplayed={5}
          marginPagesDisplayed={2}
          onPageChange={this.handlePageClick}
          containerClassName="pagination"
        />
      </div>
      </div>
    );
  }
}

export default PaginatedTable;
