import React, { Component } from 'react';
import axios from 'axios';
import ReactPaginate from 'react-paginate';
import 'bootstrap/dist/css/bootstrap.css';
import './table.css'; // Import the CSS file


class PaginatedTable extends Component {
  state = {
    data: [], // Store fetched data here
    currentPage: 0,
    itemsPerPage: 5, // Number of items to display per page
  };

  componentDidMount() {
    this.fetchData();
  }

  fetchData = () => {
    axios.get(`http://localhost:8050/api/v1/getallbookmarks?page=${this.state.currentPage}&pageSize=${this.state.itemsPerPage}`)
      .then((response) => {
        this.setState({
          data: response.data,
        });
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  };

  handlePageClick = (selectedPage) => {
    this.setState({
      currentPage: selectedPage.selected,
    });
  };



  // componentDidMount() {
  //   fetch(`http://localhost:8050/api/v1/getallbookmarks?page=${this.state.currentPage}&pageSize=${this.stateitemsPerPage}`) // Replace with your API endpoint
  //     .then((response) => response.json())
  //     .then((data) => this.setState({ data }));
  // }
  handleNextClick = () => {
    // Increment the current page by 1 if not on the last page
    if (this.state.currentPage < Math.ceil(this.state.data.length / this.state.itemsPerPage) - 1) {
      this.setState((prevState) => ({
        currentPage: prevState.currentPage + 1,
      }));
    }
  };


  render() {
    // Pagination logic (skip in this example)
    const { data, currentPage, itemsPerPage } = this.state;

    const startIndex = currentPage * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    const currentData = data.slice(startIndex, endIndex);


    return (
      <div className="table-container">
        <table>
          <thead>
            <tr>
            <th>S.no</th>
            <th>Name</th>
            <th>Email</th>
            <th>Description</th>
            <th>Link / Details</th>
            <th>User</th>
            <th>tagName</th>
            </tr>
          </thead>
          <tbody>
            {currentData.map((item, index) => (
              <tr key={index}>
                <td>{index}</td>
                <td>{item.name}</td>
                <td>{item.description}</td>
                <td>{item.link}</td>
                <td>{item.user.email}</td>
                <td>{item.tag.name}</td>
                {/* Add more table cells based on your data structure */}
              </tr>
            ))}
          </tbody>
        </table>
        <div className="pagination-container">
          <ReactPaginate
            pageCount={Math.ceil(data.length / itemsPerPage)}

            containerClassName="pagination"
          />
        </div>
      </div>
    );
  }
}

export default PaginatedTable;
