import React, { Component } from 'react';
import axios from 'axios';
import ReactPaginate from 'react-paginate';
import 'bootstrap/dist/css/bootstrap.css';
import './table.css'; // Import the CSS file


class PaginatedTable extends Component {
  state = {
    data: [],          // Store fetched data
    currentPage: 0,    // Current page number
    itemsPerPage: 10, // Number of items to display per page
    pageCount: 0
  };

  componentDidMount() {
    this.fetchData();
  }

  fetchData = () => {
    const { currentPage, itemsPerPage } = this.state;
    const offset = currentPage * itemsPerPage;

    // Replace 'your_api_endpoint' with your actual API endpoint
    axios.get(`http://localhost:8050/api/v1/getallbookmarks?page=${offset}&limit=${itemsPerPage}`)
      .then((response) => {
        this.setState({
          data: response.data.items,
          pageCount: Math.ceil(response.data.totalCount / itemsPerPage),
        });
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  };

  handlePageClick = (selectedPage) => {
    this.setState(
      { currentPage: selectedPage.selected },
      () => this.fetchData()
    );
  };

  render() {
    // Pagination logic (skip in this example)
    const { data, currentPage, itemsPerPage } = this.state;

    const startIndex = currentPage * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    const currentData = data.slice(startIndex, endIndex);


    return (
      <div className="table-container">
        <table>
          <thead>
            <tr>
            <th>S.no</th>
            <th>Name</th>
            <th>Email</th>
            <th>Description</th>
            <th>Link / Details</th>
            <th>User</th>
            <th>tagName</th>
            </tr>
          </thead>
          <tbody>
            {currentData.map((item, index) => (
              <tr key={index}>
                <td>{index}</td>
                <td>{item.name}</td>
                <td>{item.description}</td>
                <td>{item.link}</td>
                <td>{item.user.email}</td>
                <td>{item.tag.name}</td>
                {/* Add more table cells based on your data structure */}
              </tr>
            ))}
          </tbody>
        </table>
        <div className="pagination-container">
          <ReactPaginate
            pageCount={this.state.itemsPerPage}
            pageRangeDisplayed={5}
            marginPagesDisplayed={2}
            onPageChange={this.handlePageClick}
            containerClassName="pagination"
          />
        </div>
      </div>
    );
  }
}

export default PaginatedTable;
